import{_ as l,c as o,o as i,al as c}from"./chunks/framework.6-m_a-od.js";const p=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"tech-learning/Web3/北大肖臻《区块链技术与应用》/笔记02-BTC-密码学原理.md","filePath":"技术学习/Web3/北大肖臻《区块链技术与应用》/笔记02-BTC-密码学原理.md","lastUpdated":1764513386000}'),t={name:"tech-learning/Web3/北大肖臻《区块链技术与应用》/笔记02-BTC-密码学原理.md"};function d(a,e,r,s,n,_){return i(),o("div",null,[...e[0]||(e[0]=[c("<ul><li><code>crypto currency</code>：加密货币</li><li>比特币中主要用到密码学中两个功能： <ul><li>哈希</li><li>签名</li></ul></li><li>密码学中用到的哈希函数被称为：<code>cryptographic hash function</code>，有两个重要性质： <ul><li><code>collision resistance</code>：不是说不会出现hash碰撞，而是说没有什么高效的方法人为的制造哈希碰撞。</li><li><code>hiding</code>：哈希函数的计算过程是单向的，不可逆的。</li><li>两者结合起来可以实现：<code>digital commitment</code>，又叫<code>digital equivalent ofa sealed envelope</code>。</li><li>比特币中用到的第三个性质：<code>puzzle friendly</code>：哈希值的计算事先是不可预测的。可以用来做<code>proof of work</code>（工作量证明）。挖矿很难，但是证明很容易。<code>difficult to solve，but easy to verify</code>。</li></ul></li><li>比特币中用的哈希函数：<code>SHA-256</code>。<code>SHA</code>即<code>secure hash algorithm</code>。 <ul><li><code>asymmetric encryption algorithm</code>：非对称加密算法。</li></ul></li><li>比特币中账户系统，本地创建一个公钥私钥的密钥对即可。用私钥对交易进行签名，然后别人用公钥对交易进行验证。 <ul><li>比特币中一般是对一个message先进行hash，然后对hash值进行签名。</li></ul></li></ul>",1)])])}const h=l(t,[["render",d]]);export{p as __pageData,h as default};
